    ;WITH rs AS
    (
    SELECT col.value(&#39;(GroupKey/text())[1]&#39;, &#39;VARCHAR(MAX)&#39;) AS [Before]
    FROM @xml.nodes(&#39;/PWR_ViewAll/dgvViewAll_Vertical&#39;) AS tab (col)
    --WHERE  CHARINDEX(@li, col.value(&#39;(GroupKey/text())[1]&#39;, &#39;VARCHAR(MAX)&#39;)) &gt; 0
